// SPDX-License-Identifier: MIT

pragma solidity ^0.8.24;

// Interface for an item fragment.
interface IItemFragment {
    // represents an item fragment owned by a player.
    struct OwnedItemFragment {
        // if the item fragment is owned by the player.
        bool owned;
        // the item fragment's numerical data
        //
        // BIT POSITIONS:
        // [0 - 127] - the item fragment's ID (128 bits)
        // [128 - 159] - the amount of this item fragment owned by the player (32 bits)
        // [160 - 255] - additional numerical data (96 bits)
        uint256 numData;
    }

    function getItemFragments(address player, uint256[] calldata fragmentIds) external view returns (OwnedItemFragment[] memory);
    function addItemFragmentToInventory(
        address player, 
        OwnedItemFragment calldata fragment,
        // [0] - salt
        // [1] - adminSig
        bytes[2] calldata sigData
    ) external;
    function updateItemFragmentNumData(
        address player,
        // [0] - fragmentId
        // [1] - numData
        uint256[2] calldata data,
        // [0] - salt
        // [1] - adminSig
        bytes[2] calldata sigData
    ) external;
    function removeItemFragmentFromInventory(
        address player,
        uint256 fragmentId,
        // [0] - salt
        // [1] - adminSig
        // [2] - playerSig
        bytes[3] calldata sigData
    ) external;
}

// SPDX-License-Identifier: MIT

pragma solidity ^0.8.24;

// Interface for item fragment-related errors.
interface IItemFragmentErrors {
    /**
     * @dev Thrown when trying to add an item fragment the user already owns; prevents any overwriting.
     */
    error ItemFragmentAlreadyOwned(uint256 fragmentId);

    /**
     * @dev Thrown when trying to remove an item fragment the user doesn't own; prevents any unauthorized removals.
     */
    error ItemFragmentNotOwned(uint256 fragmentId);
}

contracts/wonderchamps/items/Item.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.24;

import "./IItem.sol";
import "./IItemErrors.sol";
import "./IItemFragmentErrors.sol";

// Item contract containing item- and item fragment-related operations.
abstract contract Item is IItem, IItemErrors, IItemFragmentErrors { 
    /**
     * @dev Mask to extract the item ID from an item's {numData} or an item fragment's {numData}.
     *
     * NOTE: Both items and item fragments store the ID in the lower 128 bits of the {numData}, so a singular mask can be used.
     */
    uint256 internal constant _ITEM_ID_MASK = (1 << 128) - 1;

    /**
     * @dev Gets the item ID from an item's {numData} or an item fragment's ID from an item fragment's {numData}.
     */
    function _getItemID(uint256 numData) internal pure returns (uint256) {
        return numData & _ITEM_ID_MASK;
    }
}

contracts/wonderchamps/player/IPlayer.so.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.24;

// import "../inventory/IInventory.sol";
// import "../stats/IInGameStats.sol";

import "../items/IItem.sol";
import "../items/IItemFragment.sol";
import "../stats/ILeagueData.sol";

// Interface for a player's data.
interface IPlayer is IItem, IItemFragment, ILeagueData {
    /**
     * @dev Represents a player instance. Mainly used when retrieving player data.
     *
     * For specifics on what each field represents, please refer to the Player contract.
     */
    struct Player {
        uint256 ownedIGC;
        OwnedItem[] items;
        OwnedItemFragment[] fragments;
        uint256 drawingStats;
        LeagueData[] leagueData;
    }

    function getPlayer(
        address player,
        uint256[] calldata itemIDs,
        uint256[] calldata fragmentIDs,
        uint256[] calldata leagueSeasons
    ) external view returns (Player memory);
    function getOwnedIGC(address player) external view returns (uint256);
    function playerExists(address player) external view returns (bool);
    function createPlayer(address player, bytes32 salt, uint256 timestamp, bytes calldata adminSig) external;
    function deletePlayer(
        address player, 
        uint256[] calldata ownedItemIDs,
        uint256[] calldata ownedItemFragmentIDs,
        uint256[] calldata leagueSeasons,
        bytes32 salt, 
        uint256 timestamp, 
        bytes[2] calldata sigs
    ) external;
}

